CREATE COLUMN TABLE "SYSTEM"."INFO_BARRAGEM" (
    "ID" INT NOT NULL PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
    "NOME_BARRAGEM" NVARCHAR(50) NOT NULL,
    "CONSTRUCAO" NVARCHAR(50) NOT NULL,
    "REJEITO" NVARCHAR(50) NOT NULL,
    "DESCRICAO" NVARCHAR(200) NOT NULL
);

CREATE COLUMN TABLE "SYSTEM"."MQTT_DATA"(
	"ID" INT NOT NULL GENERATED BY DEFAULT AS IDENTITY,
	"REFERENCIA" INT,
	"DISTANCIA" DOUBLE,
	"NIVEL" INT,
	"UMIDADE_PERCENTUAL" DOUBLE,
	"TIMESTAMP" NVARCHAR(50),
	PRIMARY KEY ("ID")
);

ALTER TABLE "SYSTEM"."MQTT_DATA"
ADD CONSTRAINT "MQTT_DATA_INFO_BARRAGEM_FK" (INFO_BARRAGEM_ID_INFO)
REFERENCES "SYSTEM"."INFO_BARRAGEM" (INFO_BARRAGEM_ID_INFO);

